import React from 'react';
import './App.css';

function App() {
  const letters = [
    { letter: 'A', word: 'Apple', image: 'üçé', phonetic: '/Àà√¶/', translation: 'Manzana' },
    { letter: 'B', word: 'Ball', image: 'üèÄ', phonetic: '/b…îÀêl/', translation: 'Pelota' },
    { letter: 'C', word: 'Cat', image: 'üê±', phonetic: '/k√¶t/', translation: 'Gato' }
  ];

  return (
    <div className="App">
      <h1>Learn the Alphabet with Yei J.J.</h1>
      <p>Hi! I'm Yei J.J. Let's learn the ABCs together!</p>
      <div className="letters">
        {letters.map(({ letter, word, image, phonetic, translation }) => (
          <div key={letter} className="card">
            <h2>{letter}</h2>
            <div className="icon">{image}</div>
            <p>{word} <span className="phonetic">({phonetic})</span></p>
            <div className="tooltip">Translation: {translation}</div>
          </div>
        ))}
      </div>
    </div>
  );
}

export default App;
